    $db->setQuery((string) $query);
    $messages = $db->loadObjectList();

    $options = array();

    if ($messages)
    {
        foreach ($messages as $message)
        {
            $options[] = JHtml::_('select.option', $message->id, $message->location_text);
        }
    }

    $options = array_merge(parent::getOptions(), $options);

    return $options;

