        $v_b = '';
        $v_e = '';
        $b2a = '';
        $b2  = '';
    }
    if ($v_e == 0)
    {
        $v_e = '';
        $b2a = '';
    }
    if ($ch_e == 0)
    {
        $b2a  = '';
        $ch_e = '';

        if ($v_e == 0)
        {
            $b3 = '';
        }
    }
    $scripture = $book . $b1 . $ch_b . $b2 . $v_b . $b3 . $ch_e . $b2a . $v_e;
}
