(()=>{window.jSelectTeachers=(id,title,catid,object,link,lang)=>{let hreflang="";if(!Joomla.getOptions("xtd-locations")){return false}const{editor}=Joomla.getOptions("xtd-locations");if(lang!==""){hreflang=`hreflang="${lang}"`}const tag=`<a ${hreflang} href="${link}">${title}</a>`;window.parent.Joomla.editors.instances[editor].replaceSelection(tag);if(window.parent.Joomla.Modal){window.parent.Joomla.Modal.getCurrent().close()}return true};document.addEventListener("DOMContentLoaded",()=>{const elements=document.querySelectorAll(".select-link");for(let i=0,l=elements.length;l>i;i+=1){elements[i].addEventListener("click",event=>{event.preventDefault();const{target}=event;const functionName=target.getAttribute("data-function");if(functionName==="jSelectLocations"){window[functionName](target.getAttribute("data-id"),target.getAttribute("data-title"),target.getAttribute("data-cat-id"),null,target.getAttribute("data-uri"),target.getAttribute("data-language"))}else{window.parent[functionName](target.getAttribute("data-id"),target.getAttribute("data-title"),target.getAttribute("data-cat-id"),null,target.getAttribute("data-uri"),target.getAttribute("data-language"))}if(window.parent.Joomla.Modal){window.parent.Joomla.Modal.getCurrent().close()}})}})})();