(()=>{window.jSelectSeries=(id,title,object,link,lang)=>{let hreflang="";if(!Joomla.getOptions("xtd-servers")){return false}const{editor}=Joomla.getOptions("xtd-servers");if(lang!==""){hreflang=`hreflang = "${lang}"`}const tag=` < a ${hreflang} href = "${link}" > ${title} < / a > `;window.parent.Joomla.editors.instances[editor].replaceSelection(tag);if(window.parent.Joomla.Modal){window.parent.Joomla.Modal.getCurrent().close()}return true};document.addEventListener("DOMContentLoaded",()=>{const elements=document.querySelectorAll(".select-link");for(let i=0,l=elements.length;l>i;i+=1){elements[i].addEventListener("click",event=>{event.preventDefault();const{target}=event;const functionName=target.getAttribute("data-function");if(functionName==="jSelectServer"){window[functionName](target.getAttribute("data-id"),target.getAttribute("data-title"),target.getAttribute("data-uri"),target.getAttribute("data-language"))}else{window.parent[functionName](target.getAttribute("data-id"),target.getAttribute("data-title"),target.getAttribute("data-uri"),target.getAttribute("data-language"))}if(window.parent.Joomla.Modal){window.parent.Joomla.Modal.getCurrent().close();const doc=window.parent.document,theForm=doc.getElementById("adminForm"),task=doc.getElementsByName("task");for(let i=0;i<task.length;i++){task[i].value="cwmmediafile.setServer"}theForm.submit()}})}})})();